import styles from "./Button.module.css";
export default function Button({ selected, icon, text }) {
  const classBox = `${styles.buttonBox}`;
  return (
    <div className={classBox}>
      <div className={styles.button}>
        <div className={`${styles.iconBox} ${selected ? styles.selected : ""}`}>
          {icon}
        </div>
        <p className={`${styles.text} ${selected ? styles.selected : ""}`}>
          {text}
        </p>
      </div>
    </div>
  );
}
